import { useState } from "react"

export default function Form(){
    const [formData, setFormData]= useState({
        title:"",
        date:new Date().toLocaleString() + "",
        description:"",
        image: ""
    })

    function onHandleChange(e){
        const name = e.target.name
        let value = e.target.value

        setFormData({...formData,
        [name]:value
        })
    }

    function handleSubmit(e){
        e.preventDefault()
        console.log(formData)

    }

    return(
        <>
        <form onSubmit={handleSubmit}>

            <input onChange={onHandleChange} type="text" name="title" placeholder="Enter Title" value={formData.title} />

            <input type="url" name="image" placeholder="Enter Image URL" value={formData.image} />

            <textarea name="description" placeholder="Enter Description" value={formData.description} ></textarea>
            <button type="submit" className="btn-btn-outline-success">submit</button>
        </form>
        </>

    )
}
